package exemplo_calculoTrapezio;

public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double B, b, h, area;
		
		B = 8.0;
		b = 6.0;
		h = 5.0;
		
		area = (B + b) / 2.0 * h ;
		
		System.out.print("A area do trapezio equivale a " + area + "cm");
	}

}
